mapper.select:
SELECT DATE(match_date) as date, COUNT(*) as betCount, SUM(bet_amount) as totalBetAmount, SUM(CASE WHEN win_amount IS NOT NULL THEN win_amount ELSE 0 END) as totalWinAmount, SUM(CASE WHEN win_amount IS NOT NULL THEN win_amount ELSE 0 END) - SUM(bet_amount) as netProfit, COUNT(CASE WHEN result = 'WIN' THEN 1 END) as winCount, COUNT(CASE WHEN result = 'LOSE' THEN 1 END) as loseCount, COUNT(CASE WHEN result = 'DRAW' THEN 1 END) as drawCount FROM betting_record WHERE user_id = #{userId}
AND match_date >= #{startDate}
AND match_date <= #{endDate}
GROUP BY DATE(match_date) ORDER BY DATE(match_date) DESC

SELECT league, COUNT(*) as betCount, SUM(bet_amount) as totalBetAmount, SUM(CASE WHEN win_amount IS NOT NULL THEN win_amount ELSE 0 END) as totalWinAmount, SUM(CASE WHEN win_amount IS NOT NULL THEN win_amount ELSE 0 END) - SUM(bet_amount) as netProfit, COUNT(CASE WHEN result = 'WIN' THEN 1 END) as winCount, COUNT(CASE WHEN result = 'LOSE' THEN 1 END) as loseCount, COUNT(CASE WHEN result = 'DRAW' THEN 1 END) as drawCount, ROUND(COUNT(CASE WHEN result = 'WIN' THEN 1 END) * 100.0 / COUNT(*), 2) as winRate FROM betting_record WHERE user_id = #{userId}
AND match_date >= #{startDate}
AND match_date <= #{endDate}
GROUP BY league ORDER BY betCount DESC

SELECT bet_type as betType, COUNT(*) as betCount, SUM(bet_amount) as totalBetAmount, SUM(CASE WHEN win_amount IS NOT NULL THEN win_amount ELSE 0 END) as totalWinAmount, SUM(CASE WHEN win_amount IS NOT NULL THEN win_amount ELSE 0 END) - SUM(bet_amount) as netProfit, COUNT(CASE WHEN result = 'WIN' THEN 1 END) as winCount, COUNT(CASE WHEN result = 'LOSE' THEN 1 END) as loseCount, COUNT(CASE WHEN result = 'DRAW' THEN 1 END) as drawCount, ROUND(COUNT(CASE WHEN result = 'WIN' THEN 1 END) * 100.0 / COUNT(*), 2) as winRate FROM betting_record WHERE user_id = #{userId}
AND match_date >= #{startDate}
AND match_date <= #{endDate}
GROUP BY bet_type ORDER BY betCount DESC
